import { React, useState, useEffect } from "react";
import {
  Container,
  Typography,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Box,
  LinearProgress,
} from "@mui/material";

const calculateTableData = (data) => {
  return data.map((stage, index) => {
    const nextStage = data[index + 1];
    const moved = nextStage
      ? nextStage.count
      : stage.label === "Won"
      ? stage.count
      : 0;
    const lost = stage.count - moved;
    const winRate = ((data[data.length - 1].count / stage.count) * 100).toFixed(
      0
    );
    return {
      stage: stage.label,
      cameToStage: stage.count,
      lost,
      moved,
      winRate: `${winRate}%`,
    };
  });
};

function App() {
  const [data, setData] = useState(null);

  useEffect(() => {
    fetch("http://localhost:3000/api/data")
      .then((res) => res.json())
      .then(setData)
      .catch((err) => console.error("Error fetching data:", err));
  }, []);
  if (!data) {
    return <Typography>NULL</Typography>;
  }

  const tableData = calculateTableData(data);
  return (
    <Container>
      <Box>
        <Typography variant="h5" mt={6} mb={2}>
          Win Rate by Count (Bar Style)
        </Typography>
        {data.map((d) => {
          const pct = d.count;
          return (
            <Box key={d.label} mb={2}>
              <Typography>
                {d.label} ({d.count}) - {pct.toFixed(0)}%
              </Typography>
              <LinearProgress variant="indeterminate" value={pct} />
            </Box>
          );
        })}
        <Typography variant="h5" mt={6} mb={2}>
          Win Rate by Count (Bar Style)
        </Typography>
        {data.map((d) => {
          const pct = (data[data.length - 1].count / d.count) * 100;
          return (
            <Box key={d.label} mb={2}>
              <Typography>
                {d.label} ({d.count}) - {pct.toFixed(0)}%
              </Typography>
              <LinearProgress variant="determinate" value={pct} />
            </Box>
          );
        })}
      </Box>
      <Typography variant="h4" gutterBottom mt={4}>
        Funnel Table
      </Typography>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Stage</TableCell>
            <TableCell>Came to Stage</TableCell>
            <TableCell style={{ color: "#D95F02" }}>Lost</TableCell>
            <TableCell style={{ color: "#1B9E77" }}>Moved</TableCell>
            <TableCell>Win Rate %</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {tableData.map((row) => (
            <TableRow key={row.stage}>
              <TableCell>{row.stage}</TableCell>
              <TableCell>{row.cameToStage}</TableCell>
              <TableCell>{row.lost}</TableCell>
              <TableCell>{row.moved}</TableCell>
              <TableCell>{row.winRate}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </Container>
  );
}

export default App;
