import { React, useState, useEffect } from "react";
import {
  Container,
  Typography,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Box,
  Grid,
  LinearProgress,
} from "@mui/material";
import { green } from "@mui/material/colors";

const calculateTableData = (data) => {
  return data.map((stage, index) => {
    const nextStage = data[index + 1];
    const moved = nextStage
      ? nextStage.count
      : stage.label === "Won"
      ? stage.count
      : 0;
    const lost = stage.count - moved;
    const winRate = ((data[data.length - 1].count / stage.count) * 100).toFixed(
      0
    );
    return {
      stage: stage.label,
      cameToStage: stage.count,
      lost,
      moved,
      winRate: `${winRate}%`,
    };
  });
};

function App() {
  const [data, setData] = useState(null);

  useEffect(() => {
    fetch("http://localhost:3000/api/data")
      .then((res) => res.json())
      .then(setData)
      .catch((err) => console.error("Error fetching data:", err));
  }, []);
  if (!data) {
    return <Typography>NULL</Typography>;
  }

  const tableData = calculateTableData(data);
  return (
    <Container width="100%">
      <Grid container spacing={3}>
        <Grid size={{ xs: 12, sm: 6 }}>
          <Typography variant="h5" mt={6} mb={2}>
            Win Rate by Count-{" "}
            {((data[data.length - 1].count / data[0].count) * 100).toFixed(0)}%
          </Typography>
          <Table size="small">
            <TableBody>
              {data.map((d) => {
                const pct = (d.count / 24) * 100;
                return (
                  <TableRow key={d.label} mb={2}>
                    <TableCell>{d.label}</TableCell>
                    <TableCell width="100%" style={{ textAlign: "center" }}>
                      {23}
                      <Box
                        sx={{
                          position: "relative",
                          height: 20,
                          backgroundColor: "#ccc",
                          overflow: "hidden",
                        }}
                      >
                        <Box
                          sx={{
                            position: "absolute",
                            top: 0,
                            bottom: 0,
                            color: "#fff",
                            left: "50%",
                            width: `${pct}%`,
                            alignContent: "center",
                            alignItems: "center",
                            alignmentBaseline: "central",
                            transform: "translateX(-50%)",
                            backgroundColor: "#62a63a",
                          }}
                        >
                          {d.count}
                        </Box>
                      </Box>
                    </TableCell>
                    <TableCell>
                      {((data[data.length - 1].count / d.count) * 100).toFixed(
                        0
                      )}
                      %
                    </TableCell>
                  </TableRow>
                );
              })}
            </TableBody>
          </Table>
        </Grid>
        <Grid size={{ xs: 12, sm: 6 }}>
          <Typography variant="h5" mt={6} mb={2}>
            Win Rate by ACV-{" "}
            {(data[data.length - 1].acv / data[0].acv).toFixed(0)}%
          </Typography>
          <Table size="small">
            <TableBody>
              {data.map((d) => {
                const pct = (d.count / 24) * 100;
                return (
                  <TableRow key={d.label} mb={2}>
                    <TableCell>{d.label}</TableCell>
                    <TableCell width="100%" style={{ textAlign: "center" }}>
                      {23}
                      <Box
                        sx={{
                          position: "relative",
                          height: 20,
                          backgroundColor: "#ccc",
                          overflow: "hidden",
                        }}
                      >
                        <Box
                          sx={{
                            position: "absolute",
                            top: 0,
                            bottom: 0,
                            color: "#fff",
                            left: "50%",
                            width: `${pct}%`,
                            alignContent: "center",
                            alignItems: "center",
                            alignmentBaseline: "central",
                            transform: "translateX(-50%)",
                            backgroundColor: "#62a63a", // Moved color
                          }}
                        >
                          ${d.acv.toFixed(0)}
                        </Box>
                      </Box>
                    </TableCell>
                    <TableCell>
                      {((data[data.length - 1].acv / d.acv) * 100).toFixed(0)}%
                    </TableCell>
                  </TableRow>
                );
              })}
            </TableBody>
          </Table>
        </Grid>
        <Grid size={{ xs: 12, sm: 6 }}>
          <Typography variant="h4" gutterBottom mt={4}>
            Funnel Table
          </Typography>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Stage</TableCell>
                <TableCell>Came to Stage</TableCell>
                <TableCell style={{ color: "#D95F02" }}>Lost</TableCell>
                <TableCell style={{ color: "#1B9E77" }}>Moved</TableCell>
                <TableCell>Win Rate %</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {tableData.map((row) => (
                <TableRow key={row.stage}>
                  <TableCell>{row.stage}</TableCell>
                  <TableCell>{row.cameToStage}</TableCell>
                  <TableCell>{row.lost}</TableCell>
                  <TableCell>{row.moved}</TableCell>
                  <TableCell>{row.winRate}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </Grid>
        <Grid size={{ xs: 12, sm: 6 }}>
          <Typography variant="h4" gutterBottom mt={4}>
            Funnel Table
          </Typography>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Stage</TableCell>
                <TableCell>Came to Stage</TableCell>
                <TableCell style={{ color: "#D95F02" }}>Lost</TableCell>
                <TableCell style={{ color: "#1B9E77" }}>Moved</TableCell>
                <TableCell>Win Rate %</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {tableData.map((row) => (
                <TableRow key={row.stage}>
                  <TableCell>{row.stage}</TableCell>
                  <TableCell>{row.cameToStage}</TableCell>
                  <TableCell>{row.lost}</TableCell>
                  <TableCell>{row.moved}</TableCell>
                  <TableCell>{row.winRate}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </Grid>
      </Grid>
    </Container>
  );
}

export default App;
